// Login.js
import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./Login.css"; // Assuming you have a CSS file for your login styles

const Login = () => {
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = () => {
    // Use a regular expression to check if the entered email is a valid Gmail address
    const isGmail = /^[a-zA-Z0-9._-]+@gmail\.com$/.test(email);

    if (isGmail) {
      // Perform your login logic here
      // For example, you can navigate to the home page
      history.push("/home");
    } else {
      setError("Please enter a valid Gmail address");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <div className="login-form">
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default Login;
