import React, { useState } from "react";
import "./Home.css"; // Import your styles for Home if needed

const Home = () => {
  const [imageArray, setImageArray] = useState([
    "/images/image1.jpg",
    "/images/image2.jpg",
    "/images/image3.jpg",
    "/images/image4.jpg",
    "/images/image5.jpg",
  ]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];

    // Check if a file is selected
    if (file) {
      // Assuming you have a function to generate a URL for the uploaded image
      const imageUrl = URL.createObjectURL(file);

      // Update the state with the new image URL
      setImageArray((prevImages) => [...prevImages, imageUrl]);
    }
  };

  return (
    <div className="home-container">
      <h1 className="welcome-text">Welcome to the Home Page</h1>

      <div className="image-container">
        {imageArray.map((image, index) => (
          <div key={index} className="image-box">
            <img src={image} alt={`Image ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* File input for image upload */}
      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        className="add-image-input"
      />
    </div>
  );
};

export default Home;
